import React, { Component } from 'react';
import Counter from './counter';

class Counters extends Component {
    handleIncrement = counter => {
        const counters = [...this.props.counters];
        const index = counters.indexOf(counter)
        counters[index] = { ...counter };
        counters[index].value++;
        this.setState({ counters });
    }

    handleReset = () => {
        const counters = this.props.counters.map(c => {
            c.value = 0;
            return c;
        });

        this.setState({ counters });
    };

    handleDelete = counterId => {
        const counters = this.props.counters.filter(c => c.id !== counterId);
        this.setState({ counters });
    };

    render() {
        return (
            <div>
                <button onClick={this.handleReset} className="btn btn-primary btn-sm m-2">Reset</button>
                {this.props.counters.map(counter => (
                    <Counter key={counter.id} onDelete={this.handleDelete} onIncrement={this.handleIncrement} counter={counter}>
                    </Counter>
                ))}

            </div>
        );
    }
}

export default Counters;
